mapper.update:
UPDATE xxl_job_registry
SET update_time = sysdate
WHERE registry_group = #{registryGroup}
  AND registry_key = #{registryKey}
  AND registry_value = #{registryValue}

mapper.delete:
DELETE FROM xxl_job_registry
WHERE registry_group = #{registryGroup}
	AND registry_key = #{registryKey}
	AND registry_value = #{registryValue}
